    private void btnLogoutActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLogoutActionPerformed
        // TODO add your handling code here:
        workPanel.remove(this);
        CardLayout layout = (CardLayout) workPanel.getLayout();
        layout.previous(workPanel);

    }//GEN-LAST:event_btnLogoutActionPerformed

    private void btnAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddActionPerformed
        // TODO add your handling code here:
        String vin = txtVIN.getText().trim();

        if (vin.length() != 17 || !vin.matches("[A-Z0-9]+")) {
            JOptionPane.showMessageDialog(this, "Please enter a valid VIN format (17 characters, alphanumeric).", "Invalid VIN Format", JOptionPane.WARNING_MESSAGE);
            return;
        }

        if (vinDirectory.isVinExists(vin)) {
            JOptionPane.showMessageDialog(this, "VIN already exists in the directory.", "Duplicate VIN", JOptionPane.WARNING_MESSAGE);
            return;
        }

        vinDirectory.addVin(vin);

        populateVINTable();
        
        txtVIN.setText("");
    }//GEN-LAST:event_btnAddActionPerformed

    private void btnRemoveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRemoveActionPerformed
        // TODO add your handling code here:
        int selectedRow = tblVINTable.getSelectedRow();

        if (selectedRow < 0) {
            JOptionPane.showMessageDialog(this, "Please select a VIN to remove.", "No VIN Selected", JOptionPane.WARNING_MESSAGE);
            return;
        }

        String selectedVIN = (String) tblVINTable.getValueAt(selectedRow, 0);

        DefaultTableModel model = (DefaultTableModel) tblVINTable.getModel();
        model.removeRow(selectedRow);

        vinDirectory.removeVin(selectedVIN);
    }//GEN-LAST:event_btnRemoveActionPerformed


