    private void populateVINTable() {
        DefaultTableModel model = (DefaultTableModel) tblVINTable.getModel();

        model.setRowCount(0);

        for (String vin : vinDirectory.getVinList()) {
            Object[] row = {vin};
            model.addRow(row);
        }
    }
